FROM vewUserPnlDetails WHERE userId = User_ID GROUP BY matchId, MarketId, SelectionId )a Where a.pnlvalue<0  group by MarketId)X ), 0)
 + IFNULL((select SUM(bet_value)*-1  Liability from  `bet_entry` where TypeID in (3) and ResultID is null and userid = User_ID ), 0) 
 + IFNULL((select SUM(bet_value )*-1 Liability  from  `bet_entry` a inner join matchfancy b on a.fancyId = b.ID where a.TypeID = 4 and ResultID is null and userid = User_ID), 0)
 + IFNULL((SELECT SUM(minvalue) Liability  FROM (SELECT MIN(pnlvalue) minvalue, matchId, MarketId FROM (SELECT SUM(aa) pnlvalue, matchId, fancyid  MarketId, yes SelectionId FROM vewsessionliability WHERE userId = User_ID GROUP BY yes,  matchId, fancyid) X GROUP BY matchId, MarketId)  Y), 0)
