DECLARE  LID INTEGER ;
DECLARE OID INTEGER ;
SET @runtot:=0; 
set @num:=0;
	set OID = pForParent;
	SET LID = (SELECT parentid FROM `createmaster` WHERE mstrid = pUserID); 
	if pForParent = LID then
	set	pForParent = 1;
	else
	set	pForParent = 0;
