IF pType = 3 THEN
	SELECT *,(@num:=@num+1) SrNo FROM (SELECT * FROM (SELECT '' RefNo, a.MstDate EDate,b.mstruserid UserC, CASE WHEN Chips > 0 THEN Chips ELSE 0 END Credit, CASE WHEN Chips < 0 THEN ABS(Chips) ELSE 0 END Debit, (@runtot := @runtot + Chips) Total, a.selectionname narration , Odds , Stack , CASE WHEN IFNULL(a.Result,0) = 1 THEN 'Win' ELSE 'Lost' END Status_, a.Mstcode FROM tblbets a INNER JOIN createmaster b ON a.UserId=b.mstrid
