	SELECT '' RefNo, a.dateTime EDate,b.mstruserid UserC, CASE WHEN Chips > 0 THEN Chips ELSE 0 END Credit, CASE WHEN Chips < 0 THEN ABS(Chips) ELSE 0 END Debit, (@runtot := @runtot + Chips) Total, CONCAT(c.matchname ,'>',a.FheadName ) narration ,OddsNumber Odds ,bet_value Stack , '' Status_, a.bet_id Mstcode FROM bet_entry a 
INNER JOIN createmaster b ON a.UserId=b.mstrid 
