CREATE DEFINER=`dev`@`%` PROCEDURE `GetLedger`(IN `pType` INT, IN `pUserID` INT, IN `pChipType` INT, IN `pParentID` INT, IN `pFrom` DATE, IN `pTo` DATE)
BEGIN
SET @runtot:=0;
set @num:=0;
SET @Where = CONCAT(' and UserID = ', pUserID );
IF pFrom <> 'null' THEN
SET @Where = CONCAT(@Where ,' and a.MstDate between date(', pFrom , ') and date(' ,pTo,')' );
End IF;

IF pParentID > 0 THEN
SELECT *,(@num:=@num+1) SrNo FROM (
	SELECT  ROUND(ChipsCr, 2)  Credit , ROUND(ChipsDr, 2) Debit ,a.MatchId ,a.MarketId , a.MstDate EDate, narration , msttype TypeID , a.UserID, IFNULL(a.childid, 0) ChildID, ROUND((@runtot := @runtot + a.ChipsCr - a.ChipsDr), 2) AS Balance, a.Mstcode, a.CrDr, 0 as oppAcID FROM tblchipdet a
	
	WHERE a.UserID = pUserID  AND a.oppAcID =pParentID  ORDER BY a.MstDate , a.MstCode , a.CrDr) X ORDER BY EDate DESC, MstCode DESC, CrDr;
ELSE
	if pChipType = 4 Then
		SET @query = CONCAT('SELECT *,(@num:=@num+1) SrNo FROM (SELECT case when CrDr = 1 then ROUND(Chips ,2) else 0 end Credit ,case when CrDr = 2 then ROUND(Chips ,2) else 0 end Debit ,0 MatchId ,0 MarketId , MstDate EDate, Narration narration, MstType TypeID,UserID UserID  ,0 ChildID ,ROUND((@runtot := @runtot + Chips), 2) AS Balance,MstCode ,CrDr, 0 as oppAcID FROM tblchips a where 1=1 ' , @Where  , ' and BetID is null and IsFree = 1 ORDER BY MstDate , MstCode , CrDr)xx ORDER BY EDate DESC, MstCode DESC, CrDr');
	Else
	SET @Where = CONCAT(@Where ,' and case when ', pChipType ,'=2 then a.msttype in (6,9) when ',pChipType ,'=3 then a.msttype = 50 else a.msttype > 4 and a.msttype < 50 end ' );
		SET @query = CONCAT('SELECT *,(@num:=@num+1) SrNo FROM (
		SELECT  ROUND(ChipsCr, 2)  Credit , ROUND(ChipsDr, 2) Debit ,a.MatchId ,case when a.msttype = 8 then a.fancyid else a.MarketId end MarketId , a.MstDate EDate, narration , msttype TypeID , a.UserID, IFNULL(a.childid, 0) ChildID, ROUND((@runtot := @runtot + a.ChipsCr - a.ChipsDr), 2) AS Balance, a.Mstcode, a.CrDr, a.oppAcID FROM tblchipdet a
		
		WHERE 1=1 ' , @Where  , ' ORDER BY a.MstDate , a.MstCode , a.CrDr) X ORDER BY EDate DESC, MstCode DESC, CrDr');
	END IF ;
		  PREPARE stmt FROM @query;
EXECUTE stmt;
DEALLOCATE PREPARE stmt;
END IF ;
	END ;;
