CREATE DEFINER=`dev`@`%` PROCEDURE `GetLiablityHistory`(IN `pUserID` INT)
BEGIN
DECLARE pType INTEGER ;
set @num:=0;
set pType = (select usetype from createmaster where mstrid = pUserID);
IF pType = 3 THEN
SELECT *,(@num:=@num+1) SrNo FROM (SELECT a.mstcode, CONCAT(e.Name ,'>', d.MatchName ,'>', f.Name) Description, a.selectionName, CASE WHEN a.isBack = 0 THEN 'Back' ELSE 'Lay' END Type, a.Odds, a.Stack, a.MstDate, a.P_L, CASE WHEN IFNULL(a.result, 0) = 1 THEN CASE WHEN IFNULL(a.isBack, 0) = 0 THEN a.P_L ELSE  a.Stack END END Profit, CASE WHEN IFNULL(a.result, 0) = 0 THEN CASE WHEN IFNULL(a.isBack, 0) = 0 THEN a.Stack ELSE a.P_L END END Liability, CASE WHEN  a.result IS NULL THEN 'Open' ELSE 'Settled' END STATUS FROM tblbets a
    LEFT JOIN matchmst d ON a.MatchId = d.MstCode
    LEFT JOIN sportmst e ON d.SportId = e.ID
    LEFT JOIN market f ON a.MarketId =f.ID
    WHERE a.UserId = pUserID AND a.result IS NULL
    Union
