	, CASE WHEN a.OddValue = 0 THEN 'Back' ELSE 'Lay' END Type,OddsNumber Odds, bet_value Stack, a.dateTime mstdate, '' P_L, CASE WHEN IFNULL(a.Chips, 0) > 0 THEN Chips ELSE  0 END Profit, CASE WHEN IFNULL(a.Chips, 0) < 0 THEN Chips ELSE  0 END Liability,
CASE WHEN  a.ResultID IS NULL THEN 'Open' ELSE 'Settled' END STATUS FROM bet_entry a

    LEFT JOIN matchmst d ON a.matchId = d.MstCode
    LEFT JOIN sportmst e ON a.sportId = e.ID
    where userId = pUserID AND a.ResultID IS NULL
	)aa ORDER BY aa.mstdate DESC, aa.mstcode DESC;
  ELSEIF pType = 2 THEN
  SELECT *,(@num:=@num+1) SrNo FROM (SELECT a.mstcode, CONCAT(e.Name ,'>', d.MatchName ,'>', f.Name) Description, a.selectionName, CASE WHEN a.isBack = 0 THEN 'Back' ELSE 'Lay' END Type, a.Odds, a.Stack, a.MstDate, a.P_L, CASE WHEN IFNULL(a.result, 0) = 1 THEN CASE WHEN IFNULL(a.isBack, 0) = 0 THEN a.P_L ELSE  a.Stack END END Profit, CASE WHEN IFNULL(a.result, 0) = 0 THEN CASE WHEN IFNULL(a.isBack, 0) = 0 THEN a.Stack ELSE a.P_L END END Liability, CASE WHEN  a.result IS NULL THEN 'Open' ELSE 'Settled' END STATUS, b.mstruserid UserNm FROM tblbets a
    left join createmaster b on a.UserID = b.mstrid
    LEFT JOIN matchmst d ON a.MatchId = d.MstCode
    LEFT JOIN sportmst e ON d.SportId = e.ID
    LEFT JOIN market f ON a.MarketId =f.ID
    WHERE a.ParantId = pUserID AND a.ResultID IS NULL
    Union
    SELECT a.bet_id mstcode, CONCAT('>>',d.MatchName,'>>',a.FheadName,'') Description, '' selectionName
, CASE WHEN a.OddValue = 0 THEN 'Back' ELSE 'Lay' END Type,
OddsNumber Odds, bet_value Stack, a.dateTime mstdate, '' P_L, CASE WHEN IFNULL(a.Chips, 0) > 0 THEN Chips ELSE  0 END Profit, CASE WHEN IFNULL(a.Chips, 0) < 0 THEN Chips ELSE  0 END Liability,
CASE WHEN  a.ResultID IS NULL THEN 'Open' ELSE 'Settled' END STATUS , b.mstruserid UserNm FROM bet_entry a
  left join createmaster b on a.UserID = b.mstrid
    LEFT JOIN matchmst d ON a.matchId = d.MstCode
    where ParantId = pUserID AND a.ResultID IS NULL
	)aa ORDER BY aa.mstdate DESC, aa.mstcode DESC;
  ELSEIF pType = 1 THEN

  SELECT *,(@num:=@num+1) SrNo FROM (SELECT a.mstcode, CONCAT(e.Name ,'>', d.MatchName ,'>', f.Name) Description, a.selectionName, CASE WHEN a.isBack = 0 THEN 'Back' ELSE 'Lay' END Type, a.Odds, a.Stack, a.MstDate, a.P_L, CASE WHEN IFNULL(a.result, 0) = 1 THEN CASE WHEN IFNULL(a.isBack, 0) = 0 THEN a.P_L ELSE  a.Stack END END Profit, CASE WHEN IFNULL(a.result, 0) = 0 THEN CASE WHEN IFNULL(a.isBack, 0) = 0 THEN a.Stack ELSE a.P_L END END Liability, CASE WHEN  a.result IS NULL THEN 'Open' ELSE 'Settled' END STATUS, b.mstruserid UserNm ,c.mstruserid DealerNm   FROM tblbets a
  left join createmaster b on a.UserID = b.mstrid
left join createmaster c on a.ParantID = c.mstrid
    LEFT JOIN matchmst d ON a.MatchId = d.MstCode
    LEFT JOIN sportmst e ON d.SportId = e.ID
    LEFT JOIN market f ON a.MarketId =f.ID AND a.ResultID IS NULL
    WHERE a.ParantId IN (SELECT Mstrid  FROM createmaster WHERE usetype = 2 AND parentID = pUserID)
    Union
    SELECT a.bet_id mstcode, CONCAT(e.Name ,'>', d.MatchName,'>', a.FheadName ,'>', IFNULL(m.result,'')) Description, '' selectionName
, CASE WHEN a.OddValue = 0 THEN 'Back' ELSE 'Lay' END Type,
OddsNumber Odds, bet_value Stack, a.dateTime mstdate, '' P_L, CASE WHEN IFNULL(a.Chips, 0) > 0 THEN Chips ELSE  0 END Profit, CASE WHEN IFNULL(a.Chips, 0) < 0 THEN Chips ELSE  0 END Liability,
CASE WHEN  a.ResultID IS NULL THEN 'Open' ELSE 'Settled' END STATUS, b.mstruserid UserNm ,c.mstruserid DealerNm   FROM bet_entry a
 left join createmaster b on a.UserID = b.mstrid
left join createmaster c on a.ParantID = c.mstrid
left JOIN matchfancy m ON a.fancyID =m.ID
    LEFT JOIN matchmst d ON a.matchId = d.MstCode
    LEFT JOIN sportmst e ON a.sportId = e.ID
    where ParantId IN (SELECT Mstrid  FROM createmaster WHERE usetype = 2 AND parentID = pUserID) AND a.ResultID IS NULL
	)aa ORDER BY aa.mstdate DESC, aa.mstcode DESC;
  ELSEIF pType = 0 THEN
    SELECT *,(@num:=@num+1) SrNo FROM (SELECT a.mstcode, CONCAT(e.Name ,'>', d.MatchName ,'>', f.Name) Description, a.selectionName, CASE WHEN a.isBack = 0 THEN 'Back' ELSE 'Lay' END Type, a.Odds, a.Stack, a.MstDate, a.P_L, CASE WHEN IFNULL(a.result, 0) = 1 THEN CASE WHEN IFNULL(a.isBack, 0) = 0 THEN a.P_L ELSE  a.Stack END END Profit, CASE WHEN IFNULL(a.result, 0) = 0 THEN CASE WHEN IFNULL(a.isBack, 0) = 0 THEN a.Stack ELSE a.P_L END END Liability, CASE WHEN  a.result IS NULL THEN 'Open' ELSE 'Settled' END STATUS, b.mstruserid UserNm ,c.mstruserid DealerNm  FROM tblbets a
