    COMMENT 'User Matched bets listing'
IF pMarketID = 0 THEN
SELECT *  FROM (SELECT a.mstcode, CONCAT(e.Name ,'>', d.MatchName ,'>', f.Name) Description,d.MatchName,f.Name MarketName,a.IsMatched,a.MarketId,a.selectionName, CASE WHEN a.isBack = 0 THEN 'Back' ELSE 'Lay' END Type, a.Odds, a.Stack, ROUND(UNIX_TIMESTAMP( CONVERT_TZ(a.MstDate,'+05:30','+00:00') )) MstDate, a.P_L, CASE WHEN IFNULL(a.result, 0) = 1 THEN CASE WHEN IFNULL(a.isBack, 0) = 0 THEN a.P_L ELSE  a.Stack END ELSE 0 END Profit, CASE WHEN IFNULL(a.result, 0) = 0 THEN CASE WHEN IFNULL(a.isBack, 0) = 0 THEN a.Stack ELSE a.P_L END END Liability, CASE WHEN  a.result IS NULL THEN 'Open' ELSE 'Settled' END STATUS FROM tblbets a 
    LEFT JOIN matchmst d ON a.MatchId = d.MstCode 
    LEFT JOIN sportmst e ON d.SportId = e.ID 
    LEFT JOIN market f ON a.MarketId =f.ID 
    WHERE a.MatchId = pMatchID AND a.UserId = pUserID AND a.IsMatched = 1
 Union
    SELECT a.bet_id mstcode, CONCAT(e.name,'>',d.MatchName,'>',a.FheadName,'') Description,d.MatchName,a.FheadName MarketName,'1' IsMatched ,'' MarketId,CASE WHEN a.OddValue = 0 THEN 'Yes' ELSE 'No' END selectionName
