CASE WHEN  a.ResultID IS NULL THEN 'Open' ELSE 'Settled' END STATUS FROM bet_entry a 

    LEFT JOIN matchmst d ON a.matchId = d.MstCode 
    LEFT JOIN sportmst e ON a.sportId = e.ID 
    where userId = pUserID AND a.matchId = pMatchID AND a.ResultID IS NULL  
    )aa ORDER BY aa.mstdate DESC, aa.mstcode DESC;
