CREATE DEFINER=`dev`@`%` PROCEDURE `InsPartner`(IN `pTypeID` INT, IN `pParentID` INT, IN `pUserID` INT, IN `pAdmin` DECIMAL, IN `pMaster` DECIMAL, IN `pDealer` DECIMAL)
    NO SQL
BEGIN
 
DECLARE EXIT HANDLER FOR SQLEXCEPTION
  BEGIN
      ROLLBACK;
END;
DECLARE EXIT HANDLER FOR SQLWARNING
 BEGIN
     ROLLBACK;
END;
 
START TRANSACTION;
 
IF (SELECT Dealer FROM tblpartner WHERE UserID = pUserID AND TypeID = pTypeID) IS NULL THEN
     IF (pTypeID = 3) THEN  
			INSERT INTO tblpartner (TypeID ,ParentID,UserID,Admin,MASTER,Dealer) 
		SELECT 3,pParentID,pUserID,Admin,MASTER,Dealer FROM tblpartner WHERE UserID = pParentID AND TypeID = 2;  
	ELSE 
			INSERT INTO tblpartner (TypeID ,ParentID,UserID,Admin,MASTER,Dealer)
			VALUES (pTypeID ,pParentID,pUserID,pAdmin,pMaster,pDealer);  
	END IF;
ELSE 
    UPDATE tblpartner SET Admin = pAdmin , MASTER = pMaster, Dealer = pDealer
	WHERE  UserID = pUserID AND TypeID = pTypeID ;  	

	IF pTypeID = 1 THEN
		UPDATE tblpartner SET Admin = pAdmin , MASTER = pMaster - Dealer
		WHERE  ParentID = pUserID AND TypeID = 2 ;  		
	END IF; 

END IF; 

COMMIT;

END ;;
