CREATE DEFINER=`dev`@`%` PROCEDURE `mbdip_acStatement`(IN `pUserId` INT, IN `qlimit` INT, IN `qoffset` INT)
BEGIN
SET @variable = 0;
select *,ROUND((@variable := @variable + `Chips`),2)  AS `Balance` from (
select ROUND(UNIX_TIMESTAMP(T0.MstDate)) 'Sdate', 'D/W Coins'  type ,T0.Chips,T0.Narration ,ifnull((Select l1.Chips from TBLCHIPS l1 Where l1.UserId = T0.UserId and l1.Crdr = 1 and L1.MstDate= T0.Mstdate ),0.0) 'Credit'
,ifnull((Select l1.Chips from TBLCHIPS l1 Where l1.UserId = T0.UserId and l1.Crdr = 2 and L1.MstDate= T0.Mstdate ),0.0) 'Debit','-1' as IsBack,'-1' as isFancy,0 matchId,0 MarketId,0 fancyId
from TBLCHIPS T0
INNER JOIN createmaster T1 On T1.mstrid =T0.UserId
Where T0.UserId = pUserId AND Chips != 0 

Union All
select ROUND(UNIX_TIMESTAMP(T0.MstDate)) 'Sdate', 'P/L Market' type  ,SUM(T0.Chips) Chips,Concat((SELECT Concat (CASE WHEN SportID = 1 THEN "Soccer" WHEN SportID = 2 THEN "Tennis" WHEN SportID = 4 THEN "Cricket" WHEN SportID = 7 THEN "Horse racing" ELSE "" END, ' / ' , MatchName )
FROM matchmst Where MstCode = MatchId),' / ',(select Name from market where Id = MarketId) )'Narration' ,
CASE WHEN SUM(T0.Chips) > 0 THEN SUM(T0.Chips) ELSE "0" END 'Credit'
,CASE WHEN SUM(T0.Chips) < 0 THEN SUM(T0.Chips) ELSE "0" END 'Debit', IsBack,'0' as isFancy, T0.MatchId matchId,T0.MarketId MarketId,0 fancyId
from TblBets T0
INNER JOIN createmaster T1 On T1.mstrid =T0.UserId
Where T0.UserId = pUserId GROUP BY MarketId


Union All
select ROUND(UNIX_TIMESTAMP(T0.DateTime)) 'Sdate','P/L Market' type ,SUM(T0.Chips) Chips,Concat((SELECT Concat('Cricket' , ' / ' , MatchName )
FROM matchmst Where MstCode = T0.matchId),' / ',(Select k0.HeadName from matchfancy k0 Where k0.MatchId =T0.MatchId and T0.FancyId = K0.Id ) ) 'Narration' ,CASE WHEN SUM(T0.Chips) > 0 THEN SUM(T0.Chips) ELSE "0" END 'Credit'
,CASE WHEN SUM(T0.Chips) < 0 THEN SUM(T0.Chips) ELSE "0" END 'Debit', Oddvalue as isBack,'1' as isFancy, T0.matchId matchId,0 MarketId,T0.fancyId fancyId
from bet_entry T0
INNER JOIN createmaster T1 On T1.mstrid =T0.UserId
Where T0.UserId = pUserId GROUP BY T0.fancyId
  
Union All
select ROUND(UNIX_TIMESTAMP(tcd.MstDate)) 'Sdate', 'Commision' type  , (CASE WHEN tcd.ChipsCr > 0  THEN tcd.ChipsCr WHEN tcd.ChipsDr > 0 THEN (-1 * tcd.ChipsDr)  ELSE "0" END) Chips,tcd.Narration ,
 tcd.ChipsCr 'Credit'
,tcd.ChipsDr 'Debit', '0' IsBack,'0' as isFancy , tcd.MatchId matchId,tcd.MarketId MarketId,tcd.FancyId fancyId
from tblchipdet tcd
INNER JOIN createmaster cm On cm.mstrid =tcd.UserId
Where tcd.UserId = pUserId AND tcd.MstType IN ('6','9')
 ) as t order by t.Sdate DESC LIMIT qoffset,qlimit ;
END ;;
