CREATE DEFINER=`dev`@`%` PROCEDURE `p_l_by_match`(IN `pMatchId` INT)
BEGIN

  delete from p_l_by_match where MatchId = pMatchId;

   INSERT `p_l_by_match` ( `mstruserid`, `sport_id`, `usetype`, `UserId`, `EventName`, `PnL`, `Comm`, `matchId`) SELECT M.mstruserid,d.SportID,M.usetype,UserId, d.MatchName EventName,round( PnL,2)PnL, ROUND( Comm,2)Comm,  a.matchId FROM  (SELECT `a`.`UserID` AS `UserID`,
