    SET retMess = CONCAT("ERROR ", @errno, " (", @sqlstate, "): ", @text);     
    ROLLBACK;
    SELECT resultV, retMess;
END;
BEGIN
 
START TRANSACTION;
 
update tblconfig set  Marquee = pMsg where Id = 1  ; 

  
COMMIT;
SET resultV = 0;
	SET retMess = 'Saved Successfully ...';
	SELECT resultV, retMess;
END;
	END ;;
