select cast(CONVERT_TZ(tcd.MstDate,'+00:00','+05:30') as Datetime)'Sdate', 'Commision' type  , (CASE WHEN tcd.ChipsCr > 0  THEN tcd.ChipsCr WHEN tcd.ChipsDr > 0 THEN (-1 * tcd.ChipsDr)  ELSE "0" END) Chips,tcd.Narration ,
