  (SELECT IFNULL(SUM(-Stack),0 )P_L FROM `tblbets` WHERE result =0 AND isBack = 0 AND UserID =pUserID)g,
  (SELECT IFNULL(SUM(Stack),0 )P_L FROM `tblbets` WHERE result =0 AND isBack = 1 AND UserID =pUserID)h,
  (Select sum(X .mSum)Liability from(select min(a.pnlvalue)'mSum' from (SELECT SUM(winValue + lossValue) pnlvalue, matchId, MarketId, SelectionId 
FROM vewUserPnlDetails WHERE userId = pUserID GROUP BY matchId, MarketId, SelectionId )a Where a.pnlvalue<0  group by MarketId)X ) i , 
    (SELECT  SUM(IFNULL(ChipsCr, 0) - IFNULL(ChipsDr, 0)) ChipBal FROM tblchipdet WHERE UserID =  pUserID )j, 
    (SELECT SUM(minvalue) Liability  FROM (SELECT MIN(pnlvalue) minvalue, matchId, MarketId FROM (SELECT SUM(aa) pnlvalue, matchId, fancyid  MarketId, yes SelectionId FROM vewsessionliability WHERE userId = pUserID GROUP BY yes,  matchId, fancyid) X GROUP BY matchId, MarketId)  Y) k,  
  (select SUM(bet_value)*-1  Liability from  `bet_entry` where TypeID in (3) and ResultID is null and userid = pUserID )L,   
    (SELECT SUM(minvalue)  Liability  FROM (SELECT MIN(pnlvalue) minvalue, matchId, MarketId FROM (SELECT SUM(aa) pnlvalue, matchId, fancyid  MarketId, runvalue SelectionId FROM vewUpDownliability WHERE userId = pUserID GROUP BY runvalue,  matchId, fancyid) X1 GROUP BY matchId, MarketId)  Y1) M,
    (select SUM(bet_value )*-1 Liability  from  `bet_entry` a inner join matchfancy b on a.fancyId = b.ID where a.TypeID = 4 and ResultID is null and userid = pUserID)N,
  (SELECT SUM(winValue) Liability  FROM  vewUserPnlUnMOnly WHERE userId = pUserID ) O,
  (select ifnull(Liability, 0) * case when ifnull(Liability, 0) > 0 then -1 else 1 end Liability from (select SUM(case when OddValue = 1 then 1 else -1 end * bet_value) Liability from `bet_entry` where TypeID in (1) and ResultID is null and userid = pUserID) qryOEinner) qryOddEven) src 
    SET dest.liability = src.Liability,dest.balance = src.Balance,dest.p_l = src.P_L,dest.freechips = src.FreeChip,dest.chip = src.Chip,dest.sessionLiability = src.sessionLiability,dest.unmatchliability = src.unmatchliability where dest.mstrid = pUserID;
	
