      ) MstDate, 
      bet.MstDate orgDate, 
      bet.IsMatched, 
      bet.MarketId, 
      bet.SelectionId, 
      bet.MatchId, 
      bet.MstCode, 
      bet.P_L, 
      bet.UserId, 
      matmst.matchName 
    FROM 
      tblbets bet 
      INNER JOIN createmaster crlgn ON bet.UserId = crlgn.mstrid 
      LEFT JOIN matchmst matmst ON bet.MatchId = matmst.MstCode 
      LEFT JOIN createmaster crPid ON bet.ParantId = crPid.mstrid 
