      ) ELSE a.FHeadName END selectionName, 
      a.OddsNumber Odds, 
      a.bet_value Stack, 
      CASE WHEN (
        a.TypeID = 5 
        OR a.TypeID = 2
      ) 
      AND OddValue = 0 THEN 0 ELSE 1 END isBack, 
      DATE_FORMAT(a.dateTime, "%d-%b-%Y %H:%i:%S") MstDate, 
      a.dateTime orgDate, 
      1 IsMatched, 
      a.TypeID MarketId, 
