CREATE DEFINER=`dev`@`%` PROCEDURE `SP_GetBettingLatest`(IN `pUserId` INT, IN `pMatchId` INT)
SELECT *  FROM (SELECT a.mstcode,a.MatchId, CONCAT(e.Name ,'>', d.MatchName ,'>', f.Name) Description,d.MatchName,f.Name MarketName,a.IsMatched,a.MarketId,a.selectionName, CASE WHEN a.isBack = 0 THEN 'Back' ELSE 'Lay' END Type, a.Odds, a.Stack, ROUND(UNIX_TIMESTAMP( CONVERT_TZ(a.MstDate,'+05:30','+00:00') )) MstDate, a.P_L, CASE WHEN IFNULL(a.result, 0) = 1 THEN CASE WHEN IFNULL(a.isBack, 0) = 0 THEN a.P_L ELSE  a.Stack END ELSE 0 END Profit, CASE WHEN IFNULL(a.result, 0) = 0 THEN CASE WHEN IFNULL(a.isBack, 0) = 0 THEN a.Stack ELSE a.P_L END END Liability, CASE WHEN  a.result IS NULL THEN 'Open' ELSE 'Settled' END STATUS,'N' isFancy FROM tblbets a 
    LEFT JOIN matchmst d ON a.MatchId = d.MstCode 
    LEFT JOIN sportmst e ON d.SportId = e.ID 
    LEFT JOIN market f ON a.MarketId =f.ID 
    WHERE a.MatchId = pMatchID AND a.UserId = pUserID
 Union
    SELECT a.bet_id mstcode,a.matchId, CONCAT(e.name,'>',d.MatchName,'>',a.FheadName,'') Description,d.MatchName,a.FheadName MarketName,'1' IsMatched ,'' MarketId,CASE WHEN a.OddValue = 0 THEN 'Yes' ELSE 'No' END selectionName
    , CASE WHEN a.OddValue = 0 THEN 'Back' ELSE 'Lay' END Type,OddsNumber Odds, bet_value Stack, ROUND(UNIX_TIMESTAMP( CONVERT_TZ(a.dateTime,'+05:30','+00:00') )) MstDate, '0' P_L, CASE WHEN IFNULL(a.Chips, 0) > 0 THEN Chips ELSE  0 END Profit, CASE WHEN IFNULL(a.Chips, 0) < 0 THEN Chips ELSE  0 END Liability, 
CASE WHEN  a.ResultID IS NULL THEN 'Open' ELSE 'Settled' END STATUS,'Y' isFancy FROM bet_entry a 
left JOIN matchfancy m ON a.fancyID =m.ID 
    LEFT JOIN matchmst d ON a.matchId = d.MstCode 
    LEFT JOIN sportmst e ON a.sportId = e.ID 
    where userId = pUserID AND a.matchId = pMatchID AND a.ResultID IS NULL  
    )aa ORDER BY aa.mstdate DESC, aa.mstcode DESC LIMIT 5 ;;
