CREATE DEFINER=`dev`@`%` PROCEDURE `sp_GetP_L`(IN `pUserId` INT, IN `pSportID` INT)
BEGIN
set @num:=0;

	SELECT M.mstruserid,M.usetype,UserId, d.MatchName EventName, ifnull(e.Name, f.HeadName) MarketName ,round( PnL,2)PnL, ROUND( Comm,2)Comm, date_format(maxdate,'%d/%m/%Y %r') CreatedOn, (@num:=@num+1) SrNo, a.matchId, a.MarketId, a.fancyId FROM  (SELECT `a`.`UserID` AS `UserID`,
        SUM((CASE
            WHEN (`a`.`MstType` IN (5 , 8)) THEN (`a`.`ChipsCr` - `a`.`ChipsDr`)
            ELSE 0
        END)) AS `Pnl`,
        SUM((CASE
            WHEN (`a`.`MstType` IN (6 , 9)) THEN (`a`.`ChipsCr` - `a`.`ChipsDr`)
            ELSE 0
        END)) AS `Comm`,
        `a`.`MatchId` AS `matchId`,
        IFNULL(`a`.`MarketId`, 0) AS `MarketId`,
        IFNULL(`a`.`FancyId`, 0) AS `fancyId`,
        MAX(`a`.`MstDate`) AS `maxdate`
    FROM
        `tblchipdet` `a` WHERE UserId = pUserId
    GROUP BY `a`.`UserID` , `a`.`MatchId` , IFNULL(`a`.`MarketId`, 0) , IFNULL(`a`.`FancyId`, 0) order by MAX(`a`.`MstDate`) desc) a
inner join matchmst d on a.MatchId = d.MstCode 
inner join createmaster M on a.UserId = M.mstrid 
left join market e on a.MarketId = e.Id
left join matchfancy f on a.FancyId = f.ID
where case when pSportID = 0 then IFNULL(a.FancyID ,0) <> 0 else IFNULL(a.MarketID ,0) <> 0 and d.SportID = pSportID  end  
;
END ;;
