CREATE DEFINER=`dev`@`%` PROCEDURE `sp_GetScorePosition_Kaddal`(IN `pUserid` INT, IN `pFancyId` INT, IN `pMatchId` INT)
BEGIN
DECLARE  iMax INTEGER ;
DECLARE  iMin INTEGER ;
DECLARE lUseType INTEGER;
SET lUseType = (SELECT usetype FROM createmaster WHERE mstrid = pUserID);
IF lUseType = 3 THEN
	select MAX(oddValue)+oddsNumber +5,MIN(oddValue)-5 into iMax,iMin from bet_entry where Typeid =3 and  userID = pUserid and MatchID = pMatchId and FancyID = pFancyId;
	
	select RunValue , round(SUM(a), 2) a from (
	select  RunValue ,case when  OddValue <= RunValue  and (OddValue +oddsNumber) > RunValue  then bet_value else -bet_value end a from `sessionruns` a
	join bet_entry b
	where Typeid =3 and  userID = pUserid and MatchID = pMatchId and FancyID = pFancyId and RunValue between iMin  and iMax)a
	group by RunValue ;
ELSEIF lUseType = 2 THEN
	select MAX(oddValue)+oddsNumber +5 , MIN(oddValue)-5 into  iMax,iMin from bet_entry where Typeid =3 and  parantId = pUserid and MatchID = pMatchId and FancyID = pFancyId;
		
	select RunValue , round(SUM(a), 2) a from (
	select  RunValue ,case when  OddValue <= RunValue  and (OddValue +oddsNumber) > RunValue then -bet_value else bet_value end * ifnull(Dealer, 0) / 100 a from `sessionruns` a
	join bet_entry b
	where Typeid =3 and  parantId = pUserid and MatchID = pMatchId and FancyID = pFancyId and RunValue between iMin  and iMax)a
	group by RunValue ;
ELSEIF lUseType = 1 THEN
	select MAX(oddValue)+oddsNumber +5 , MIN(oddValue)-5 into  iMax,iMin from bet_entry where Typeid =3 and  parantId IN (SELECT Mstrid  FROM createmaster WHERE usetype = 2 AND parentID = pUserid) and MatchID = pMatchId and FancyID = pFancyId;
	select RunValue , round(SUM(a), 2) a from (
	select  RunValue ,case when  OddValue <= RunValue  and (OddValue +oddsNumber) > RunValue  then -bet_value else bet_value end * ifnull(Master, 0) / 100 a from `sessionruns` a
	join bet_entry b
	where Typeid =3 and  parantId IN (SELECT Mstrid  FROM createmaster WHERE usetype = 2 AND parentID = pUserid) and MatchID = pMatchId and FancyID = pFancyId and RunValue between iMin  and iMax)a
	group by RunValue ;
ELSEIF lUseType = 0 THEN
	select MAX(oddValue)+oddsNumber +5 , MIN(oddValue)-5 into  iMax,iMin from bet_entry where Typeid =3  and MatchID = pMatchId and FancyID = pFancyId;
	
	select RunValue , round(SUM(a), 2) a from (
	select  RunValue ,case when  OddValue <= RunValue  and (OddValue +oddsNumber) > RunValue  then -bet_value else bet_value end * ifnull(Admin, 0) / 100 a from `sessionruns` a
	join bet_entry b
	where Typeid =3 and MatchID = pMatchId and FancyID = pFancyId and RunValue between iMin  and iMax)a
	group by RunValue ;
END IF;
