    NO SQL
BEGIN 
	IF (p_userType = 3) THEN
		SELECT SUM(TeamA) AS TeamA, SUM(TeamB) AS TeamB, SUM(theDraw) AS theDraw, p_userId AS UserId, mstruserid, b.usetype TypeId FROM vewUserHomePanel a INNER JOIN createmaster b ON b.mstrid = a.userId WHERE userId = p_userId AND marketId = p_marketId AND matchId = p_matchId ;
    ELSE IF (p_userType = 2) THEN
		SELECT ROUND(SUM(TeamA * -1),2) AS TeamA, ROUND(SUM(TeamB * -1),2) AS TeamB, ROUND(SUM(theDraw * -1),2) AS theDraw, UserId, b.mstruserid, b.usetype  TypeId FROM vewUserHomePanel a INNER JOIN createmaster b ON b.mstrid = a.UserId WHERE parantId = p_userId AND marketId = p_marketId AND matchId = p_matchId   GROUP BY UserId, b.mstruserid, b.usetype ORDER BY b.mstruserid;
    ELSE IF (p_userType = 1) THEN
		SELECT ROUND(SUM(TeamA * -1  * (ADMIN+MASTER) / 100),2)  AS TeamA, ROUND(SUM(TeamB * -1 * (ADMIN+MASTER) / 100),2) AS TeamB, ROUND(SUM(theDraw * -1 * (ADMIN+MASTER) / 100),2) AS theDraw, b.mstrid AS UserId, b.mstruserid, b.usetype  TypeId FROM vewUserHomePanel a  INNER JOIN createmaster b ON b.mstrid = a.parantid  WHERE b.ParentID = p_userId AND marketId = p_marketId AND matchId = p_matchId    GROUP BY b.mstrid, b.usetype ORDER BY b.mstruserid;
	ELSE
		SELECT round(SUM(TeamA * -1 * (ADMIN) / 100),2) AS TeamA, round(SUM(TeamB * -1 * (ADMIN) / 100), 2) AS TeamB, round(SUM(theDraw * -1 * (ADMIN) / 100),2) AS theDraw, c.ParentID AS UserId, b.mstruserid, b.usetype TypeId FROM vewUserHomePanel a  INNER JOIN createmaster c ON c.mstrid = a.parantid INNER JOIN createmaster b ON b.mstrid = c.ParentID WHERE marketId = p_marketId AND matchId = p_matchId  GROUP BY c.parentId, b.mstruserid, b.usetype  ORDER BY b.mstruserid;    
	END IF;
