CREATE DEFINER=`dev`@`%` PROCEDURE `sp_get_p_l_by_match_id_filters`(IN `pMatchId` INT, IN `pUserId` INT, IN `pEventName` VARCHAR(50), IN `pFromDate` DATETIME, IN `pToDate` DATETIME)
BEGIN
    declare pEventNameConditions varchar(255) default '';
    declare pDateConditions varchar(255) default '';
    declare finalQuery varchar(60000) default '';


    if pEventName != '' THEN
      SET pEventNameConditions =  CONCAT(" and d.MatchName LIKE'%",pEventName,"%' ");
    else
      SET pEventNameConditions = '';
    end if;




    if pToDate != '0000-00-00' AND pFromDate != '0000-00-00' THEN
      SET pDateConditions = CONCAT(" and ( maxdate >= '",pFromDate,"' AND maxdate <= '",pToDate,"' ) ");
    else
      SET pDateConditions =  '';
    end if;


    SET @finalQuery = CONCAT(
        "SELECT `a`.`is_deleted`,M.mstruserid,M.usetype,UserId, d.bet_deleted, d.MatchName EventName, ifnull(e.Name, f.HeadName) MarketName ,round( PnL,2)PnL, ROUND( Comm,2)Comm, date_format(maxdate,'%d/%m/%Y %r') CreatedOn, (@num:=@num+1) SrNo, a.matchId, a.MarketId, a.fancyId FROM  (SELECT `a`.`is_deleted`,`a`.`UserID` AS `UserID`,
               SUM((CASE
                   WHEN (`a`.`MstType` IN (5 , 8)) THEN (`a`.`ChipsCr` - `a`.`ChipsDr`)
                   ELSE 0
               END)) AS `Pnl`,
               SUM((CASE
                   WHEN (`a`.`MstType` IN (6 , 9)) THEN (`a`.`ChipsCr` - `a`.`ChipsDr`)
                   ELSE 0
               END)) AS `Comm`,
               `a`.`MatchId` AS `matchId`,
               IFNULL(`a`.`MarketId`, 0) AS `MarketId`,
               IFNULL(`a`.`FancyId`, 0) AS `fancyId`,
               MAX(`a`.`MstDate`) AS `maxdate`
           FROM
               `tblchipdet` `a` WHERE `a`.is_deleted = 'N' AND UserId = ", pUserId , " and a.MatchId = ", pMatchId , " GROUP BY `a`.`UserID` , `a`.`MatchId` , IFNULL(`a`.`MarketId`, 0) , IFNULL(`a`.`FancyId`, 0) order by MAX(`a`.`MstDate`) desc) a
inner join matchmst d on a.MatchId = d.MstCode
inner join createmaster M on a.UserId = M.mstrid
left join market e on a.MarketId = e.Id
left join matchfancy f on a.FancyId = f.ID where 1=1"  , pDateConditions , pEventNameConditions );



    PREPARE stmt FROM @finalQuery;
    EXECUTE stmt;
    DEALLOCATE PREPARE stmt;


  END ;;
