CREATE DEFINER=`dev`@`%` PROCEDURE `sp_MemberTree`(
  IN `p_nodeId` INT,
  IN `p_Helper` INT

)
BEGIN

  DECLARE m_nodeSr INTEGER ;

  DECLARE m_nodeId INTEGER ;

  DECLARE m_paid INTEGER ;

  DECLARE m_bone INTEGER ;

  DECLARE m_pareId INTEGER ;

  DECLARE m_userId VARCHAR(50) ;

  DECLARE m_sideVl VARCHAR(1) ;

  DECLARE m_leveNo INTEGER ;

  DECLARE m_sponId INTEGER ;

  DECLARE m_mySide VARCHAR(1) ;

  DECLARE m_nodeDt DATETIME ;

  DECLARE m_lgnlck TINYINT ;

  DECLARE m_lgncls TINYINT ;

  DECLARE m_commision decimal(5,2);

  DECLARE m_partnership decimal(5,2);

  DECLARE m_userName VARCHAR(50) ;

  DECLARE m_mstrremarks VARCHAR(255) ;

  DECLARE m_stakelimit INTEGER ;
  DECLARE m_sessionMaxStake INTEGER ;

  DECLARE m_maxprof INTEGER ;

  DECLARE m_maxloss INTEGER ;

  DECLARE m_sescomm decimal(5,2);

  DECLARE m_othcomm decimal(5,2);
  DECLARE m_inplaystack INTEGER ;
  DECLARE m_create_no_of_child INTEGER ;
  DECLARE m_session_delay INTEGER ;

  DECLARE mset_timeout INTEGER;

  DECLARE finished INTEGER DEFAULT 0;



  DECLARE  ctr INTEGER;

  DECLARE  bFlag INTEGER;

  DECLARE  leveC INTEGER;








  SET  leveC = 1 ;

  SET  bFlag = 1 ;

  SET  ctr = 1  ;

  SET m_nodeSr = 0;



  DROP TABLE IF EXISTS arrList ;

  DROP TABLE IF EXISTS tmpFinalList ;

  DROP TABLE IF EXISTS finalList ;

  DROP TABLE IF EXISTS processLists ;

  DROP TABLE IF EXISTS tmpList ;



  CREATE TEMPORARY TABLE arrList (mstruserid VARCHAR(50),mstrname VARCHAR(50),mstrremarks VARCHAR(255), mstrid INTEGER , mstrlock INTEGER, usetype INTEGER, parentid INTEGER, leveNo INTEGER, mstrcrdate DATETIME,lgnusrlckbtng TINYINT,lgnusrCloseAc  TINYINT,Commission decimal(5,2),partner decimal(5,2),  lgnUserMaxStake INTEGER,sessionMaxStake INTEGER,lgnUserMaxProfit INTEGER ,lgnUserMaxLoss INTEGER,SessionComm decimal(5,2),OtherComm decimal(5,2),set_timeout INTEGER,InPlayStack INTEGER,create_no_of_child INTEGER ,session_delay INTEGER) ;





  CREATE TEMPORARY TABLE tmpFinalList( nodeSr INTEGER NOT NULL AUTO_INCREMENT ,mstruserid VARCHAR(50),mstrname VARCHAR(50), mstrremarks VARCHAR(255), mstrid INTEGER , mstrlock INTEGER, usetype INTEGER, parentid INTEGER, leveNo INTEGER, mstrcrdate DATETIME,lgnusrlckbtng TINYINT,lgnusrCloseAc  TINYINT,Commission decimal(5,2),partner decimal(5,2),lgnUserMaxStake INTEGER,sessionMaxStake INTEGER,lgnUserMaxProfit INTEGER ,lgnUserMaxLoss INTEGER,SessionComm decimal(5,2),OtherComm decimal(5,2),set_timeout INTEGER,InPlayStack INTEGER,create_no_of_child INTEGER, session_delay INTEGER ,PRIMARY KEY (nodeSr)) ;

  CREATE TEMPORARY TABLE finalList ( nodeSr INTEGER NOT NULL  , mstruserid VARCHAR(50),mstrname VARCHAR(50),mstrremarks VARCHAR(255), mstrid INTEGER , mstrlock INTEGER, usetype INTEGER, parentid INTEGER, leveNo INTEGER, mstrcrdate DATETIME ,lgnusrlckbtng TINYINT,lgnusrCloseAc  TINYINT,Commission decimal(5,2),partner decimal(5,2),lgnUserMaxStake INTEGER,sessionMaxStake INTEGER,lgnUserMaxProfit INTEGER ,lgnUserMaxLoss INTEGER,SessionComm decimal(5,2),OtherComm decimal(5,2),set_timeout INTEGER,InPlayStack INTEGER,create_no_of_child INTEGER, session_delay INTEGER) ;

  CREATE TEMPORARY TABLE processLists ( mstruserid VARCHAR(50),mstrname VARCHAR(50),mstrremarks VARCHAR(255), mstrid INTEGER , mstrlock INTEGER, usetype INTEGER, parentid INTEGER, leveNo INTEGER, mstrcrdate DATETIME,lgnusrlckbtng TINYINT,lgnusrCloseAc  TINYINT,Commission decimal(5,2),partner decimal(5,2),lgnUserMaxStake INTEGER,sessionMaxStake INTEGER,lgnUserMaxProfit INTEGER ,lgnUserMaxLoss INTEGER,SessionComm decimal(5,2),OtherComm decimal(5,2),set_timeout INTEGER,InPlayStack INTEGER,create_no_of_child INTEGER,session_delay INTEGER) ;

  CREATE TEMPORARY TABLE  tmpList (mstruserid VARCHAR(50),mstrname VARCHAR(50),mstrremarks VARCHAR(255), mstrid INTEGER , mstrlock INTEGER, usetype INTEGER, parentid INTEGER, leveNo INTEGER, mstrcrdate DATETIME,lgnusrlckbtng TINYINT,lgnusrCloseAc  TINYINT,Commission decimal(5,2),partner decimal(5,2),lgnUserMaxStake INTEGER,sessionMaxStake INTEGER,lgnUserMaxProfit INTEGER ,lgnUserMaxLoss INTEGER,SessionComm decimal(5,2),OtherComm decimal(5,2),set_timeout INTEGER,InPlayStack INTEGER,create_no_of_child INTEGER,session_delay INTEGER) ;



  INSERT  finalList  (nodeSr, mstruserid,mstrname ,mstrremarks, mstrid, mstrlock, usetype, parentid, leveNo, mstrcrdate,lgnusrlckbtng,lgnusrCloseAc,Commission,partner,lgnUserMaxStake,sessionMaxStake,lgnUserMaxProfit,lgnUserMaxLoss,SessionComm,OtherComm,set_timeout,InPlayStack,create_no_of_child ,session_delay )

  SELECT 0,  mstruserid,mstrname, IFNULL(mstrremarks, '') mstrremarks , mstrid, mstrlock, usetype, parentid, 0, bt.mstrcrdate,lgnusrlckbtng,lgnusrCloseAc,Commission, partner,lgnUserMaxStake,sessionMaxStake,lgnUserMaxProfit,lgnUserMaxLoss,SessionComm,OtherComm,set_timeout,InPlayStack,create_no_of_child,session_delay FROM createmaster bt WHERE bt.lgnusrCloseAc =1 and bt.mstrid= p_nodeId  ;

  if p_helper <> 0 then

    INSERT  processLists SELECT mstruserid,mstrname,IFNULL(mstrremarks, '') mstrremarks, mstrid, mstrlock, usetype, bt.parentid, 1, bt.mstrcrdate,lgnusrlckbtng,lgnusrCloseAc,Commission, partner,lgnUserMaxStake,sessionMaxStake,lgnUserMaxProfit,lgnUserMaxLoss,SessionComm,OtherComm ,set_timeout,InPlayStack,create_no_of_child,session_delay FROM createmaster bt inner join tblHelper th on bt.mstrid = th.masterid  WHERE bt.lgnusrCloseAc =1 and bt.ParentID= p_nodeId and th.Helperid = p_helper ORDER BY mstrid ;

  else

    INSERT  processLists SELECT mstruserid,mstrname,IFNULL(mstrremarks, '') mstrremarks, mstrid, mstrlock, usetype, bt.parentid, 1, bt.mstrcrdate,lgnusrlckbtng,lgnusrCloseAc,Commission, partner,lgnUserMaxStake,sessionMaxStake,lgnUserMaxProfit,lgnUserMaxLoss,SessionComm,OtherComm,set_timeout,InPlayStack,create_no_of_child,session_delay  FROM createmaster bt WHERE bt.lgnusrCloseAc =1 and bt.ParentID= p_nodeId ORDER BY mstrid ;

  end if;



  INSERT  tmpFinalList ( mstruserid,mstrname,mstrremarks, mstrid, mstrlock, usetype, parentid, leveNo, mstrcrdate,lgnusrlckbtng,lgnusrCloseAc,Commission, partner,lgnUserMaxStake,sessionMaxStake,lgnUserMaxProfit,lgnUserMaxLoss,SessionComm,OtherComm,set_timeout,InPlayStack,create_no_of_child,session_delay ) SELECT * FROM  processLists ;

  SET ctr := (SELECT  COUNT(*) AS ctrs FROM  tmpFinalList) ;

  IF  ctr = 0 THEN

    BEGIN

      SET  bFlag = 0  ;

    END;

  END IF ;

  WHILE bFlag = 1  DO

  BEGIN

    SET  leveC =  leveC + 1 ;

    BEGIN

      DECLARE tree_cursor1 CURSOR

        FOR SELECT * FROM  processLists ;

      DECLARE CONTINUE HANDLER FOR NOT FOUND SET finished = 1 ;

      SET finished = 0 ;

      OPEN tree_cursor1 ;

      innerLoop1: REPEAT

        BEGIN

          FETCH tree_cursor1 INTO m_userId,m_userName,m_mstrremarks ,m_nodeId ,  m_paid,  m_bone,  m_pareId,  m_leveNo,  m_nodeDt,m_lgnlck ,m_lgncls,m_commision,m_partnership,m_stakelimit,m_sessionMaxStake,m_maxprof,m_maxloss,m_sescomm,m_othcomm,mset_timeout,m_inplaystack,m_create_no_of_child,m_session_delay;



          IF finished THEN

            LEAVE innerLoop1;

          END IF;

          INSERT  tmpList SELECT mstruserid,mstrname,IFNULL(mstrremarks, '') mstrremarks, mstrid, mstrlock, usetype, bt.parentid, 1,  mstrcrdate ,lgnusrlckbtng,lgnusrCloseAc,Commission,partner,lgnUserMaxStake,sessionMaxStake,lgnUserMaxProfit,lgnUserMaxLoss ,SessionComm,OtherComm,set_timeout,InPlayStack,create_no_of_child,session_delay

          FROM createmaster bt  WHERE bt.lgnusrCloseAc =1 and bt.ParentID= m_nodeId ORDER BY mstrid ;

        END;

      UNTIL finished

        END REPEAT ;

      CLOSE tree_cursor1 ;

      SET finished = 0;

    END;

    SET ctr := (SELECT COUNT(*) AS ctrs FROM  tmpList) ;

    IF  ctr = 0 THEN

      BEGIN

        SET  bFlag = 0  ;

        DELETE FROM  processLists ;

        DELETE FROM  tmpList ;

      END;

    ELSE

      BEGIN

        INSERT  tmpFinalList ( mstruserid,mstrname,mstrremarks, mstrid, mstrlock, usetype, parentid, leveNo, mstrcrdate,lgnusrlckbtng,lgnusrCloseAc,Commission,partner,lgnUserMaxStake,sessionMaxStake,lgnUserMaxProfit,lgnUserMaxLoss,SessionComm,OtherComm,set_timeout,InPlayStack,create_no_of_child,session_delay) SELECT * FROM  tmpList ;

        DELETE FROM  processLists ;

        INSERT  processLists SELECT * FROM  tmpList ;

        DELETE FROM  tmpList  ;

      END ;

    END IF ;

  END;

  END WHILE;

  INSERT  finalList  (nodeSr, mstruserid,mstrname,mstrremarks, mstrid, mstrlock, usetype, parentid, leveNo, mstrcrdate,lgnusrlckbtng,lgnusrCloseAc,Commission,partner,lgnUserMaxStake,sessionMaxStake,lgnUserMaxProfit,lgnUserMaxLoss,SessionComm,OtherComm,set_timeout,InPlayStack,create_no_of_child,session_delay)   SELECT * FROM tmpFinalList ORDER BY mstrcrdate, mstrid ;

  SELECT case when b.TypeID = 1 then b.Master else b.Dealer end partner ,a.*,

         CASE WHEN usetype=0 AND mstrlock=0 THEN 'app/dist/img/lock_file.png'

              ELSE CASE WHEN usetype=0 AND mstrlock=1 THEN 'app/dist/img/file.png'

                        ELSE CASE WHEN usetype=1 AND mstrlock=0 THEN 'app/dist/img/lock_master.png'

                                  ELSE CASE WHEN usetype=1 AND mstrlock=1 THEN 'app/dist/img/master.png'

                                            ELSE CASE WHEN usetype=2 AND mstrlock=0 THEN 'app/dist/img/lock_dealer.png'

                                                      ELSE CASE WHEN usetype=2 AND mstrlock=1 THEN 'app/dist/img/dealer.png'

                                                                ELSE CASE WHEN usetype=3 AND mstrlock=0 THEN 'app/dist/img/lock_user.png'

                                                                          ELSE CASE WHEN usetype=3 AND mstrlock=1 THEN 'app/dist/img/user.png'

                                                                            END END END END END END END END AS image FROM finalList a left join tblpartner b on a.mstrid = b.UserID



  order by parentid,mstruserid;

END ;;
