CREATE DEFINER=`dev`@`%` PROCEDURE `SP_OddsProfitLoss`(IN `p_userId` INT, IN `p_marketId` VARCHAR(20))
    NO SQL
BEGIN
	select  *, 
ifnull((select  (ifnull(sum(case when isBack = 0 then p_l else -p_l end),0)*ifnull(partner,0)) /100 from 
tblbets a inner join createmaster b on  a.UserId = b.MstrID where SelectionId = tblSelection.selectionId and MarketId = tblSelection.marketId and UserId=p_userId and IsMatched=1), 0) as winValue, 
ifnull((select (ifnull(sum(case when isBack = 0 then stack else -stack end),0)*ifnull(partner,0)) /100 from tblbets a inner join createmaster b on  a.UserId = b.MstrID where SelectionId <> tblSelection.selectionId and MarketId = tblSelection.marketId and UserId=p_userId and IsMatched=1),0) as lossValue
from tblSelection where MarketId = p_marketId;
 END ;;
