CREATE DEFINER=`dev`@`%` PROCEDURE `SP_OddsProfitLossNew`(IN `p_userId` INT, IN `p_userType` INT, IN `p_matchId` INT, IN `p_marketId` VARCHAR(20))
    NO SQL
BEGIN 
	if (p_userType = 3) then
		select ifnull(sum(winValue), 0) as winValue, IFNULL(sum(lossValue), 0) as lossValue, matchId, SelectionId, MarketId, selectionName  from vewUserPnlDetails  where userId = p_userId and marketId = p_marketId and matchId = p_matchId group by matchId, SelectionId, MarketId, selectionName;
    else if (p_userType = 2) then
		select IFNULL(sum(winValue * -1 * Dealer / 100), 0) as winValue, IFNULL(sum(lossValue * -1 * Dealer / 100), 0)   as lossValue, matchId, SelectionId, MarketId, selectionName from vewUserPnlDetails a inner join createmaster b on b.mstrid = a.UserId   where parantId = p_userId and marketId = p_marketId and matchId = p_matchId group by matchId, SelectionId, MarketId, selectionName;
    else if (p_userType = 1) then
		select IFNULL(sum(winValue * -1 * Master / 100), 0) as winValue, IFNULL(sum(lossValue * -1 * Master / 100), 0)  as lossValue, matchId, SelectionId, MarketId, selectionName from vewUserPnlDetails a inner join createmaster b on b.mstrid = a.UserId inner join createmaster c on c.mstrid = a.parantid   where c.ParentID = p_userId and marketId = p_marketId and matchId = p_matchId group by matchId, SelectionId, MarketId, selectionName;
	else
		select IFNULL(sum(winValue * -1 * Admin / 100), 0) as winValue, IFNULL(sum(lossValue * -1 * Admin / 100), 0)  as lossValue, matchId, SelectionId, MarketId, selectionName from vewUserPnlDetails a inner join createmaster b on b.mstrid = a.UserId   where marketId = p_marketId and matchId = p_matchId group by matchId, SelectionId, MarketId, selectionName;
	end if;
    end if;
    end if;
END ;;
