CREATE DEFINER=`dev`@`%` PROCEDURE `sp_P&L_ChipSumm_M`(IN `pUserID` INT, IN `pMatchID` INT, IN `pMarketID` INT, IN `pFancyID` INT)
BEGIN
SET @Type = pTypeID;
	SELECT b.mstrid UserID, usetype, mstruserid, CONCAT(case when ChildID = 0 then 'Parent' else mstrname end, ' A/c') mstrname, ROUND(SUM(a.ChipsCr-a.ChipsDr),2) Musum, 0 as msttype, ChildID from tblchipdet a inner join createmaster b on b.mstrid = a.oppAcID  where a.UserID = pUserID and a.MarketId = pMatchID and a.MatchId = pMarketID  and IFNULL(a.FancyId ,0) = pFancyID  group by b.mstrid, usetype, mstruserid, mstrname, ChildID having SUM(a.ChipsCr-a.ChipsDr) > 0
	union
	SELECT b.mstrid UserID, usetype, mstruserid, CONCAT(CASE WHEN ChildID = 0 THEN 'Parent' ELSE 'Own ' END, ' Commission') mstrname, ROUND(SUM(a.ChipsDr-a.ChipsCr),2) Musum, 6 msttype, 0 as ChildID FROM tblchipdet a INNER JOIN createmaster b ON b.mstrid = a.UserID  WHERE a.UserID = pUserID and a.MarketId = pMatchID and a.MatchId = pMarketID and IFNULL(a.FancyId ,0) = pFancyID  AND msttype in (6) GROUP BY b.mstrid, usetype, mstruserid, CONCAT(CASE WHEN ChildID = 0 THEN 'Parent' ELSE 'Own ' END, ' Commission') HAVING SUM(a.ChipsDr-a.ChipsCr) > 0
	UNION
	SELECT b.mstrid UserID, usetype, mstruserid, 'Cash' mstrname, ROUND(SUM(a.ChipsDr-a.ChipsCr),2) Musum, 0 as msttype, 0 as ChildID FROM tblchipdet a INNER JOIN createmaster b ON b.mstrid = a.UserID  WHERE a.UserID = pUserID and a.MarketId = pMatchID and a.MatchId = pMarketID and IFNULL(a.FancyId ,0) = pFancyID  AND msttype >= 50 GROUP BY b.mstrid, usetype, mstruserid, mstrname HAVING SUM(a.ChipsDr-a.ChipsCr) > 0
	UNION 	
	SELECT b.mstrid UserID, usetype, mstruserid, 'Own' mstrname, ROUND(SUM(a.ChipsDr-a.ChipsCr),2) Musum, 0 as  msttype, 0 as ChildID  from tblchipdet a inner join createmaster b on b.mstrid = a.UserID where a.UserID = pUserID and a.MarketId = pMatchID and a.MatchId = pMarketID and IFNULL(a.FancyId ,0) = pFancyID  and msttype not in (6, 50)  group by b.mstrid, usetype, mstruserid, mstrname having SUM(a.ChipsDr-a.ChipsCr) > 0
	;    
	
END ;;
