CREATE DEFINER=`dev`@`%` PROCEDURE `sp_PlaceBet`(
	IN `pLogInId` INT,
	IN `pUserId` INT,
	IN `pParantId` INT,
	IN `pMatchId` INT,
	IN `pSelectionId` INT,
	IN `pStack` INT,
	IN `pMarketId` VARCHAR(100),
	IN `pselectionName` VARCHAR(100),
	IN `pMstDate` DATETIME,
	IN `pOdds` DECIMAL(10, 2),
	IN `pP_L` DECIMAL(10, 2),
	IN `pisBack` INT,
	IN `pIsMatched` INT,
	IN `pNarration` VARCHAR(200),
	IN `pdeviceInfo` VARCHAR(100),
	IN `pIP_ADDESSS` VARCHAR(100),
	IN `pInPlayStack` INT,
	IN `pIsApp` INT,
	IN `pType` VARCHAR(100)
)
BEGIN
    DECLARE LID INTEGER;
    DECLARE lCtr integer;
    DECLARE resultV INT;
    DECLARE retMess VARCHAR(500);
    DECLARE checkBal decimal(50, 2);
    DECLARE EXIT HANDLER FOR SQLEXCEPTION
    BEGIN
      set resultV = -1;
      GET STACKED DIAGNOSTICS CONDITION 1 @sqlstate = RETURNED_SQLSTATE, @errno = MYSQL_ERRNO, @text = MESSAGE_TEXT;
      SET retMess = CONCAT("ERROR ", @errno, " (", @sqlstate, "): ", @text);
      ROLLBACK;
      select resultV, retMess;
    END;
    DECLARE EXIT HANDLER FOR SQLWARNING
    BEGIN
      set resultV = -2;
      GET STACKED DIAGNOSTICS CONDITION 1 @sqlstate = RETURNED_SQLSTATE, @errno = MYSQL_ERRNO, @text = MESSAGE_TEXT;
      SET retMess = CONCAT("ERROR ", @errno, " (", @sqlstate, "): ", @text);
      ROLLBACK;
      SELECT resultV, retMess;
    END;

    MainLabel: BEGIN
      DECLARE InStack INTEGER;
      DECLARE InTotalStack INTEGER;

      set InTotalStack = pStack + (select IFNULL(SUM(Stack), 0)Stack
                                   from tblbets
                                   where userid = pUserId
                                     and MatchId = pMatchId
                                     and MarketId = pMarketId);
      set InStack = (select IFNULL(InPlayStack, 0)InPlayStack from createmaster where mstrid = pUserId);
      IF pInPlayStack = 1 and InTotalStack > InStack and InStack > 0
      THEN
        SET resultV = -4;
        SET retMess = CONCAT("Check Stack Limit In InPlay ...", InStack);
        SELECT resultV, retMess;
        LEAVE MainLabel;
      END IF;

      SET resultV = 0;
      SET lCtr = (SELECT COUNT(*) FROM tblpartner WHERE UserID = pUserId);
      if lCtr = 0
      then
        SET resultV = -3;
        SET retMess = CONCAT("ERROR ", "No Partner for user");
        SELECT resultV, retMess;
      END IF;
      IF lCtr > 0
      THEN


        INSERT INTO tblbets (LogInId,
                             UserId,
                             ParantId,
                             MatchId,
                             SelectionId,
                             Stack,
                             MarketId,
                             selectionName,
                             MstDate,
                             Odds,
                             P_L,
                             isBack,
                             IsMatched,
                             deviceInfo,
                             IP_ADDESSS,
                             SAdmin,
                             Admin,
                             MASTER,
                             Dealer,
                             isApp,type)
        SELECT pLogInId,
               pUserId,
               a.parentid,
               pMatchId,
               pSelectionId,
               pStack,
               pMarketId,
               pselectionName,
               pMstDate,
               pOdds,
               pP_L,
               pisBack,
               pIsMatched,
               pdeviceInfo,
               pIP_ADDESSS,
               0,
               Admin,
               MASTER,
               Dealer,
               pIsApp,pType
        FROM createmaster a
